sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const{name, value, fieldValue} = event.payload;
    api.setState(name, fieldValue);
    var varnotify1 = true;
    var varnotify2 = true;
    if(api.state.first_name == ''){
        varnotify1 = false;
    }
    if(api.state.last_name == ''){
        varnotify2 = false;
    }


    api.setState('var1',varnotify1);
    api.setState('var2',varnotify2);





}
