/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
  function formatDate(inputText) {
    const months = {
        Jan: '01', Feb: '02', Mar: '03', Apr: '04', May: '05', Jun: '06',
        Jul: '07', Aug: '08', Sep: '09', Oct: '10', Nov: '11', Dec: '12'
    };

    const parts = inputText.split(' ');

    const year = parts[3];
    const month = months[parts[1]];
    const day = parts[2];

    return `${year}-${month}-${day}`;
}

function addDaysToDate(inputDateStr) {
  // Parse the input string as a Date object
  const inputDate = new Date(inputDateStr);

  // Add 10 days to the input date
  inputDate.setDate(inputDate.getDate() + 10);

  // Format the result as "YYYY-MM-DD"
  const year = inputDate.getFullYear();
  const month = String(inputDate.getMonth() + 1).padStart(2, '0');
  const day = String(inputDate.getDate()).padStart(2, '0');

  return `${year}-${month}-${day}`;
}






   const dat = new Date();
   var toddat =  dat.toString();
   var systemDate = formatDate(toddat);
   var afdate = addDaysToDate(systemDate);

    console.log(systemDate);
