/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {


const{fieldName, value, fieldValue} = event.payload;

  console.log(JSON.stringify(event.payload, null, 4));
  if(fieldName == "record1"){
    api.setState('record1', value);
    //console.log(api.state.record1);
  }
  if(fieldName == "record2"){
    api.setState('record2', value);
    //console.log(api.state.record2);
  }
  //console.log(api.state.record1);
  //console.log(api.state.record2);
}
