/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports, alert}) {
    const{name, value, fieldValue} = event.payload;
    api.setState(name, fieldValue);
    var buttonDisabled = true;
    var varnotify1 = true;
    var varnotify2 = true;
       console.log(JSON.stringify(event.payload, null, 4));
        if(!value){
        if(name == 'subject'){
            if(api.state.subject != ''){
                buttonDisabled = false;


            }
        }
        if(name == 'body'){
            if(api.state.body != ''){
                buttonDisabled = false;


            }
    }
    }
    if(api.state.subject == ''){
        varnotify1 = false;
    }
    if(api.state.body == ''){
        varnotify2 = false;
    }


    api.setState('buttonDisabled', buttonDisabled);
    api.setState('var1',varnotify1);
    api.setState('var2',varnotify2);
}
