sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    function getDayOfWeek(dateString) {
  // Parse the input date string
  const dateParts = dateString.split('-');
  const year = parseInt(dateParts[0]);
  const month = parseInt(dateParts[1]) - 1; // Months are 0-based (0 = January, 11 = December)
  const day = parseInt(dateParts[2]);

  // Create a Date object
  const date = new Date(year, month, day);

  // Array of day names
  const daysOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

  // Get the day of the week
  const dayOfWeek = daysOfWeek[date.getDay()];

  return dayOfWeek;
}

// Example usage:
const inputDate = "2023-08-06";
const dayOfWeek = getDayOfWeek(inputDate);
console.log(`The day of the week for ${inputDate} is ${dayOfWeek}.`);

}
