sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const{name, value, fieldValue} = event.payload;
    api.setState(name, fieldValue);
    var buttonLogin = true;
        if(!value){
        if(name == 'input1'){
            if(api.state.input1 != ''){
                buttonLogin = false;


            }
        }
        if(name == 'input2'){
            if(api.state.input2 != ''){
                buttonLogin = false;


            }
    }
    }

    api.setState('buttonLogin', buttonLogin);

    }
