sys_ux_data_broker_transform.script:
function transform(input) {
	if(input){
		var sd = input.input1.toString();
		var ld = input.input2.toString();

		var d1 = new GlideRecord("x_1047617_appian_0_patient");
		d1.get(sd);

		var d2 = new GlideRecord("x_1047617_appian_0_patient");
		d2.get(ld);

		var firstD = d1.getValue('input1');
		var lastD = d2.getValue('input2');

		if(firstD != null && lastD != null){
			var gr = new GlideRecord("patient");
			gr.initialize();
			gr.short_description = firstD;
			gr.description = lastD;
			gr.insert();
		}

	}
	var msg = 'success ' + gr.getvalue('number');
	return msg;
}
